import { useState } from 'react';

function CustomResponsiveNavbar({ logo, navigation_items, contact_button, ...customProps }) {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">

          {/* Logo - Left Side */}
          <div className="flex-shrink-0">
            <div className="flex items-center">
              <span className="text-2xl font-bold text-gray-800 hover:text-blue-600 transition-colors cursor-pointer">
                {logo?.value || logo}
              </span>
            </div>
          </div>

          {/* Desktop Menu - Center */}
          <div className="hidden md:block">
            <div className="flex items-center space-x-8">
              {navigation_items?.map((item, index) => (
                <a
                  key={index}
                  href={item.href}
                  className={`text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 relative group ${
                    item.active ? 'text-blue-600' : ''
                  }`}
                >
                  {item.label}
                  <span className={`absolute bottom-0 left-0 h-0.5 bg-blue-600 transition-all duration-300 group-hover:w-full ${
                    item.active ? 'w-full' : 'w-0'
                  }`}></span>
                </a>
              ))}
            </div>
          </div>

          {/* Contact Button - Right Side (Desktop) */}
          <div className="hidden md:block">
            <a
              href={contact_button?.href || '#'}
              className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg text-sm font-semibold transition-all duration-200 transform hover:scale-105 shadow-md hover:shadow-lg"
            >
              {contact_button?.text || 'Contact'}
            </a>
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="text-gray-700 hover:text-blue-600 p-2 rounded-md transition-colors"
              aria-label="Toggle menu"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d={isMenuOpen ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'}
                />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        <div className={`md:hidden transition-all duration-300 ease-in-out ${
          isMenuOpen ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0 overflow-hidden'
        }`}>
          <div className="px-2 pt-2 pb-3 space-y-1 border-t border-gray-200">
            {navigation_items?.map((item, index) => (
              <a
                key={index}
                href={item.href}
                className={`block text-gray-700 hover:text-blue-600 hover:bg-blue-50 px-3 py-2 rounded-md text-base font-medium transition-colors ${
                  item.active ? 'text-blue-600 bg-blue-50' : ''
                }`}
                onClick={() => setIsMenuOpen(false)}
              >
                {item.label}
              </a>
            ))}
            <a
              href={contact_button?.href || '#'}
              className="block bg-blue-600 hover:bg-blue-700 text-white px-3 py-2 rounded-md text-base font-semibold transition-colors mt-4 text-center"
              onClick={() => setIsMenuOpen(false)}
            >
              {contact_button?.text || 'Contact'}
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
}

function SplitWithImageHero({ headline, subheadline, primary_cta, secondary_cta, hero_image, ...customProps }) {
  return (
    <section className={`min-h-screen flex items-center ${customProps.className || ''}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          <div className="space-y-8">
            <h1 className="text-4xl md:text-6xl font-bold text-gray-900 leading-tight">
              {headline}
            </h1>
            <p className="text-xl text-gray-600 leading-relaxed">
              {subheadline}
            </p>
            <div className="flex flex-col sm:flex-row gap-4">
              <a
                href={primary_cta?.href || '#'}
                className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-4 rounded-lg text-lg font-semibold transition-colors inline-block text-center"
              >
                {primary_cta?.text || 'Get Started'}
              </a>
              {secondary_cta && (
                <a
                  href={secondary_cta.href}
                  className="border-2 border-gray-300 hover:border-gray-400 text-gray-700 px-8 py-4 rounded-lg text-lg font-semibold transition-colors inline-block text-center"
                >
                  {secondary_cta.text}
                </a>
              )}
            </div>
          </div>
          <div className="relative">
            <img
              src={hero_image?.src || '/placeholder.svg'}
              alt={hero_image?.alt || 'Hero image'}
              className="rounded-lg shadow-2xl w-full h-auto"
            />
          </div>
        </div>
      </div>
    </section>
  );
}

function SimpleFooter({ company = 'Your Company', year = '2024' }) {
  return (
    <footer className="bg-muted text-muted-foreground py-8">
      <div className="container mx-auto px-4 text-center">
        <p>&copy; {year} {company}. All rights reserved.</p>
      </div>
    </footer>
  );
}
