components.react_code:
function Hero1({ title = 'Welcome to Our Platform', subtitle = 'Build amazing experiences with our tools', buttonText = 'Get Started', buttonLink = '#' }) {
  return (
    <section className="bg-primary text-white py-20">
      <div className="container mx-auto px-4 text-center">
        <h1 className="text-4xl md:text-6xl font-bold mb-4">{title}</h1>
        <p className="text-xl mb-8">{subtitle}</p>
        <a href={buttonLink} className="bg-secondary hover:bg-secondary/80 text-white px-8 py-3 rounded-lg font-semibold transition-colors">
          {buttonText}
        </a>
      </div>
    </section>
  );
}

function Nav1({ logo = 'Logo', links = ['Home', 'About', 'Services', 'Contact'] }) {
  return (
    <nav className="bg-background border-b border-border">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <div className="text-2xl font-bold text-foreground">{logo}</div>
        <ul className="flex space-x-6">
          {links.map((link, index) => (
            <li key={index}>
              <a href={`#${link.toLowerCase()}`} className="text-foreground hover:text-primary transition-colors">
                {link}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
}

function Footer1({ company = 'Your Company', year = '2024' }) {
  return (
    <footer className="bg-muted text-muted-foreground py-8">
      <div className="container mx-auto px-4 text-center">
        <p>&copy; {year} {company}. All rights reserved.</p>
      </div>
    </footer>
  );
}
