function CustomResponsiveNavbar({ logo, navigation_items, contact_button, mobile_menu_container }) {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const getStyles = (styleObj) => ({
    className: styleObj?.tailwindCss || '',
    style: styleObj?.customCss || {}
  });

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50" data-editable="style" data-prop-path="navbar">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-14 md:h-16">

          {/* Logo */}
          <div className="flex-shrink-0">
            <span
              {...getStyles(logo)}
              data-editable="content"
              data-prop-path="logo"
            >
              {logo?.value || 'Logo'}
            </span>
          </div>

          {/* Desktop Menu */}
          <div className="hidden md:block">
            <div {...getStyles(navigation_items)} data-editable="array" data-prop-path="navigation_items">
              {navigation_items?.value?.map((item, index) => (
                <a
                  key={index}
                  href={item.active ? item.href : '#'}
                  {...getStyles(item)}
                  data-editable="content"
                  data-prop-path={`navigation_items.value.${index}`}
                >
                  {item.label}
                  <span className={`absolute bottom-0 left-0 h-0.5 bg-[#272725] transition-all duration-300 group-hover:w-full ${
                    item.active ? 'w-full' : 'w-0'
                  }`}></span>
                </a>
              ))}
            </div>
          </div>

          {/* Contact Button - Desktop */}
          <div className="hidden md:block">
            <a
              href={contact_button?.value?.active ? contact_button?.value?.href : '#'}
              {...getStyles(contact_button?.value)}
              data-editable="content"
              data-prop-path="contact_button.value"
            >
              {contact_button?.value?.text || 'Contact'}
            </a>
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="text-gray-700 hover:text-blue-600 p-2 rounded-md transition-colors"
              aria-label="Toggle menu"
            >
              <svg className="h-5 w-5 md:h-6 md:w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d={isMenuOpen ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'}
                />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        <div
          {...getStyles(mobile_menu_container?.value)}
          className={`${mobile_menu_container?.value?.tailwindCss || ''} ${
            isMenuOpen ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
          }`}
          data-editable="style"
          data-prop-path="mobile_menu_container.value"
        >
          <div className="px-2 pt-2 pb-3 space-y-1 border-t border-gray-200">
            {navigation_items?.value?.map((item, index) => (
              <a
                key={index}
                href={item.active ? item.href : '#'}
                className="block text-gray-700 hover:text-blue-600 hover:bg-blue-50 px-3 py-2 rounded-md text-base font-medium transition-colors"
                onClick={() => setIsMenuOpen(false)}
                data-editable="content"
                data-prop-path={`navigation_items.value.${index}`}
              >
                {item.label}
              </a>
            ))}
            <a
              href={contact_button?.value?.active ? contact_button?.value?.href : '#'}
              className="block bg-black hover:bg-black/80 text-white px-3 py-2 rounded-md text-base font-semibold transition-colors mt-4 text-center"
              onClick={() => setIsMenuOpen(false)}
              data-editable="content"
              data-prop-path="contact_button.value"
            >
              {contact_button?.value?.text || 'Contact'}
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
}

