function ContactForm({ title, subtitle, contact_info, form_fields, submit_button, container }) {
  const getStyles = (styleObj) => ({
    className: styleObj?.tailwindCss || '',
    style: styleObj?.customCss || {}
  });

  return (
    <section
      {...getStyles(container?.value)}
      data-editable="style"
      data-prop-path="container.value"
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-8 md:mb-12">
          <h2
            {...getStyles(title)}
            data-editable="content"
            data-prop-path="title"
          >
            {title?.value || 'Contact Title'}
          </h2>
          <p
            {...getStyles(subtitle)}
            data-editable="content"
            data-prop-path="subtitle"
          >
            {subtitle?.value || 'Contact subtitle'}
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 md:gap-12">
          <div className="order-2 lg:order-1">
            <div className="mb-6 md:mb-8">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Contact Information</h3>
              <div
                {...getStyles(contact_info?.value)}
                data-editable="content"
                data-prop-path="contact_info.value"
              >
                <p className="flex items-center text-gray-600 text-sm md:text-base">
                  <span className="mr-3 text-lg">📧</span>
                  {contact_info?.value?.email}
                </p>
                <p className="flex items-center text-gray-600 text-sm md:text-base">
                  <span className="mr-3 text-lg">📞</span>
                  {contact_info?.value?.phone}
                </p>
                <p className="flex items-center text-gray-600 text-sm md:text-base">
                  <span className="mr-3 text-lg">📍</span>
                  {contact_info?.value?.address}
                </p>
              </div>
            </div>
          </div>

          <div className="order-1 lg:order-2">
            <form
              {...getStyles(form_fields)}
              data-editable="array"
              data-prop-path="form_fields"
            >
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 md:gap-6 mb-4 md:mb-6">
                {form_fields?.value?.filter(field => field.name === 'firstName' || field.name === 'lastName').map((field, index) => (
                  <div key={index}>
                    <label className="block text-sm font-medium text-gray-700 mb-2">{field.label}</label>
                    <input
                      type={field.type}
                      {...getStyles(field)}
                      data-editable="content"
                      data-prop-path={`form_fields.value.${index}`}
                      required={field.required}
                    />
                  </div>
                ))}
              </div>

              {form_fields?.value?.filter(field => field.name === 'email').map((field, index) => (
                <div key={index} className="mb-4 md:mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">{field.label}</label>
                  <input
                    type={field.type}
                    {...getStyles(field)}
                    data-editable="content"
                    data-prop-path={`form_fields.value.${index + 2}`}
                    required={field.required}
                  />
                </div>
              ))}

              {form_fields?.value?.filter(field => field.name === 'message').map((field, index) => (
                <div key={index} className="mb-4 md:mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">{field.label}</label>
                  <textarea
                    rows={4}
                    {...getStyles(field)}
                    data-editable="content"
                    data-prop-path={`form_fields.value.${index + 3}`}
                    required={field.required}
                  ></textarea>
                </div>
              ))}

              <button
                type="submit"
                {...getStyles(submit_button?.value)}
                data-editable="content"
                data-prop-path="submit_button.value"
              >
                {submit_button?.value?.text || 'Send Message'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

