export default function ModernTransparentNavbar({ logo, navigation_items, cta_button, ...customProps }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className={`fixed w-full z-50 backdrop-blur-sm border-b border-white/10 ${customProps.className || ''}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4">
          <div className="flex-shrink-0">
            <span className="text-xl font-bold text-white">{logo.value}</span>
          </div>

          <div className="hidden md:block">
            <div className="ml-10 flex items-baseline space-x-8">
              {navigation_items.map((item, index) => (
                <a
                  key={index}
                  href={item.href}
                  className="text-white hover:text-gray-300 px-3 py-2 text-sm font-medium transition-colors"
                >
                  {item.label}
                </a>
              ))}
            </div>
          </div>

          <div className="hidden md:block">
            <a
              href={cta_button.href}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm font-medium transition-colors"
            >
              {cta_button.text}
            </a>
          </div>

          <div className="md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-white hover:text-gray-300"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={isOpen ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16M4 18h16"} />
              </svg>
            </button>
          </div>
        </div>

        {isOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 bg-black/50 backdrop-blur-sm rounded-lg mt-2">
              {navigation_items.map((item, index) => (
                <a
                  key={index}
                  href={item.href}
                  className="text-white hover:text-gray-300 block px-3 py-2 text-base font-medium"
                >
                  {item.label}
                </a>
              ))}
              <a
                href={cta_button.href}
                className="bg-blue-600 hover:bg-blue-700 text-white block px-3 py-2 rounded-md text-base font-medium mt-4"
              >
                {cta_button.text}
              </a>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
}

