function ModernSolidNavbar({ logo, navigation_items, cta_button, ...customProps }) {
  return (
    <nav className={`bg-white shadow-lg ${customProps.className || ''}`}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4">
          <div className="flex-shrink-0">
            <span className="text-xl font-bold text-gray-800">{logo.value}</span>
          </div>

          <div className="hidden md:flex items-center space-x-8">
            {navigation_items.map((item, index) => (
              <a
                key={index}
                href={item.href}
                className={`text-gray-700 hover:text-blue-600 px-3 py-2 text-sm font-medium transition-colors ${
                  item.active ? 'text-blue-600 border-b-2 border-blue-600' : ''
                }`}
              >
                {item.label}
              </a>
            ))}

            {cta_button && (
              <a
                href={cta_button.href}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm font-medium transition-colors"
              >
                {cta_button.text}
              </a>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
}

function CreativeMinimalHero({ headline, subheadline, hero_image, scroll_indicator, ...customProps }) {
  return (
    <section className={`min-h-screen flex flex-col justify-center relative ${customProps.className || ''}`}>
      <div className="max-w-4xl mx-auto px-4 text-center">
        <div className="space-y-6">
          <h1 className="text-5xl md:text-7xl font-serif font-light text-gray-900">
            {headline}
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            {subheadline}
          </p>
        </div>

        {hero_image && (
          <div className="mt-16 relative">
            <img
              src={hero_image.src}
              alt={hero_image.alt}
              className="mx-auto rounded-lg shadow-lg max-w-md"
            />
          </div>
        )}
      </div>

      {scroll_indicator && (
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2">
          <div className="animate-bounce">
            <svg className="w-6 h-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
            </svg>
          </div>
        </div>
      )}
    </section>
  );
}

function ContactCards({ headline, description, contact_methods, quick_form, ...customProps }) {
  const [formData, setFormData] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Quick form submitted:', formData);
    setIsSubmitted(true);
    setTimeout(() => setIsSubmitted(false), 3000);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <section className={`py-16 sm:py-20 lg:py-24 bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 ${customProps.className || ''}`}>
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16 sm:mb-20">
          <h2 className="text-4xl sm:text-5xl lg:text-6xl font-bold text-white mb-6">
            {headline}
          </h2>
          <p className="text-xl sm:text-2xl text-gray-300 max-w-3xl mx-auto">
            {description}
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 sm:gap-8 mb-16">
          {contact_methods.map((method, index) => (
            <div key={index} className="group">
              <div className="bg-white/10 backdrop-blur-sm border border-white/20 rounded-2xl p-6 sm:p-8 hover:bg-white/20 transition-all duration-300 hover:scale-105 hover:shadow-2xl">
                <div className="w-16 h-16 sm:w-20 sm:h-20 bg-gradient-to-br from-purple-400 to-pink-400 rounded-full flex items-center justify-center mx-auto mb-6 group-hover:scale-110 transition-transform duration-300">
                  <div className="text-white text-2xl sm:text-3xl">
                    {method.icon}
                  </div>
                </div>
                <h3 className="text-xl sm:text-2xl font-bold text-white mb-4 text-center">{method.title}</h3>
                <p className="text-gray-300 text-center mb-6">{method.description}</p>
                <div className="text-center">
                  <a 
                    href={method.link}
                    className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white font-semibold rounded-full hover:from-purple-600 hover:to-pink-600 transition-all duration-300 transform hover:scale-105"
                  >
                    {method.action_text}
                    <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="max-w-2xl mx-auto">
          <div className="bg-white/10 backdrop-blur-sm border border-white/20 rounded-2xl p-6 sm:p-8">
            <h3 className="text-2xl sm:text-3xl font-bold text-white mb-6 text-center">Quick Contact</h3>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                {quick_form.fields.slice(0, 2).map((field, index) => (
                  <div key={index}>
                    <input
                      type={field.type}
                      name={field.name}
                      required={field.required}
                      onChange={handleChange}
                      placeholder={field.placeholder}
                      className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-lg text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent backdrop-blur-sm"
                    />
                  </div>
                ))}
              </div>

              {quick_form.fields.slice(2).map((field, index) => (
                <div key={index + 2}>
                  {field.type === 'textarea' ? (
                    <textarea
                      name={field.name}
                      required={field.required}
                      onChange={handleChange}
                      placeholder={field.placeholder}
                      rows={4}
                      className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-lg text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent backdrop-blur-sm resize-none"
                    />
                  ) : (
                    <input
                      type={field.type}
                      name={field.name}
                      required={field.required}
                      onChange={handleChange}
                      placeholder={field.placeholder}
                      className="w-full px-4 py-3 bg-white/10 border border-white/30 rounded-lg text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400 focus:border-transparent backdrop-blur-sm"
                    />
                  )}
                </div>
              ))}

              <button
                type="submit"
                disabled={isSubmitted}
                className="w-full py-3 px-6 bg-gradient-to-r from-purple-500 to-pink-500 text-white font-bold rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
              >
                {isSubmitted ? 'Message Sent!' : quick_form.submit_button}
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

